import React, {Component} from "react"
import TodoForm from "./TodoForm"
import TodoItem from "./TodoItem"


const APIURL = "/api/todos"

class TodoList extends Component{
    constructor(props){
        super(props)
        this.state = {
            todos: []
        }
    }

    UNSAFE_componentWillMount(){
        this.loadTodos()
    }

    loadTodos = () => {
        fetch(APIURL, {
            mode: 'no-cors'
        })
        .then(res=>{
            if(!res.ok){
                throw Error
            }
            return res.json()
        })
        .then(todoss=>{
            this.setState({
                todos: todoss
            })
        })
        .catch(err=>{
            console.log(err)
        })
    }

    addTodo = (val)=>{
        fetch(APIURL, {
            mode: 'no-cors',
            method: 'post'
        })
        .then(res=>{
            if(!res.ok){
                throw Error
            }
            return res.json()
        })
        .then(todoss=>{
            this.setState({
                todos: todoss
            })
        })
        .catch(err=>{
            console.log(err)
        })
    }

    render(){

        const todoitemList = this.state.todos.map(todo=>{
            return (
                <TodoItem
                key = {todo._id}
                name={todo.name}
                completed={todo.completed}
                />
            )
        })
        return (
            <div className="TodoList">
                <h1>toDo List</h1>
                <TodoForm addTodo = {this.addTodo}/>
                {todoitemList}
            </div>
        )
    }
}

export default TodoList
